import React from 'react';
import './TicketResponse.css';
import { useLocation } from 'react-router-dom'; // keep using useLocation

const TicketResponse = () => {
    const location = useLocation();
    const { status, description } = location.state;
    const HandleSubmit = (e) => {
        e.preventDefault();
    };

    return (
        <div>
            <form onSubmit={HandleSubmit}>
                <label htmlFor="ticketDescription">Ticket Description:</label>
                <p id="ticketDescription">{description}</p>

                <label htmlFor="ticketStatus">Status:</label>
                <select id="ticketStatus" name="status">
                    <option value="New">New</option>
                    <option value="In Progress">In Progress</option>
                    <option value="Resolved">Closed</option>
                </select>

                <label htmlFor="response">Response:</label>
                <textarea id="response" name="response" rows="4" cols="50"></textarea>
                <button type="submit">Respond</button>
            </form>
        </div>
    );
};

export default TicketResponse;
