import React, { useState, useEffect } from 'react';
import axios from 'axios';

function useTickets() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('get_q')
      .then(response => {
        setData(response.data);
        console.log(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the data:", error);
      });
  }, []);

  return data;
}

const Ticket = ({ title, description }) => (
  <div style={styles.ticket}>
    <div style={styles.ticketTitle}>{title}</div>
    <div className="ticket-description">{description}</div>
  </div>
);

const TicketList = () => {
  const tickets = useTickets();  // Here, you're using the custom hook to fetch tickets

  return (
    <div style={styles.ticketList}>
      {tickets.map((ticket, index) => (
        <Ticket key={index} title={ticket.title} description={ticket.description} />
      ))}
    </div>
  );
};

export default TicketList;

// Assuming you have styles defined somewhere in your original code
const styles = {
  ticket: {
    // ... your styles for individual tickets
  },
  ticketTitle: {
    // ... your styles for ticket title
  },
  ticketList: {
    // ... your styles for ticket list
  },
};
